import React, { useState, useEffect } from 'react';
import axios from "axios";
import Forecast from "./components/Forecast.js";
import Today from "./components/Today.js";
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching Time...");
  const [day, setDay] = useState(" Fetching Date...");
  const [daily, setDaily] = useState("Fetching Daily Forecast...");
  const [hourly, setHourly] = useState(" Fetching Hourly Forecast...");
  const [weather, setWeather] = useState("Windy and Partly Cloudy");
  const [zone, setZone] = useState("Europe/Dublin")
  const [temp, setTemp] = useState("7.3")

  // Run scripts with component mounts
  useEffect(() => {
    getLocation();

    setInterval(() => {
      getTime();
    }, 1000);
  }, []);

  // Fahrenheit to Celsius
  const fToC = (temp) => {
    const celsius = Math.floor((temp - 32) / 1.8);
    return celsius;
  }

  const getWeather = async (latitude, longitude) => {
    const forecast = await axios.get(`https://api.darksky.net/forecast/90f6dc370d1bbd73bc4e6216147431ff/${latitude},${longitude}`);
    let time = new Date(forecast.data.currently.time);
    let hours = time.getDay();
    console.log(hours)

    setWeather(forecast.data.currently);
    setDaily(forecast.data.daily);
    setHourly(forecast.data.hourly);
    setZone(forecast.data.timezone);
    setTemp(fToC(forecast.data.currently.temperature));

  }

  // Changes single digit number into double. ie: 1 => 01
  const setNum = (num) => {
    let number = num;
    switch (num) {
      case 0: number = "00";
        break;
      case 1: number = "01";
        break;
      case 2: number = "02";
        break;
      case 3: number = "03";
        break;
      case 4: number = "04";
        break;
      case 5: number = "05";
        break;
      case 6: number = "06";
        break;
      case 7: number = "07";
        break;
      case 8: number = "08";
        break;
      case 9: number = "09";
        break;
    }

    return number;
  }


  // Get Current Time
  const getTime = () => {
    // Variables
    const date = new Date();
    let hour = setNum(date.getHours());
    let minute = setNum(date.getMinutes());
    let second = setNum(date.getSeconds());
    const day = date.getDay();
    let dayMonth = setNum(date.getDate());
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Set State
    setTime(`${hour} : ${minute} : ${second}`);
    setDay(`${days[day]}, ${dayMonth}`);
  };



  // Get Location
  const getLocation = () => {
    let msg;
    const success = (position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;

      // Passes location values to Weather API
      getWeather(latitude, longitude);
    }

    const error = () => {
      "Helpppp"
    }

    if (!navigator.geolocation) {
      msg = 'Geolocation is not supported by your browser';
    } else {
      msg = 'Locating…';
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }



  return (
    <>
      <Forecast weather={weather} zone={zone} temp={temp} />
      <Today day={day} daily={daily} fToC={fToC} />
      {/* Hourly */}
      {/* Daily */}
    </>
  );
}

export default App;
