import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching...");
  const [day, setDay] = useState(" Fetching...");


  // run scripts with component mounts
  useEffect(() => {
    setInterval(() => {
      getTime();
    }, 1000);
  });

  // Changes single digit day into double. ie: 1 => 01
  const setNum = (num) => {
    switch (true) {
      case 0: num = "00";
        break;
      case 1: num = "01";
        break;
      case 2: num = "02";
        break;
      case 3: num = "03";
        break;
      case 4: num = "04";
        break;
      case 5: num = "05";
        break;
      case 6: num = "06";
        break;
      case 7: num = "07";
        break;
      case 8: num = "08";
        break;
      case 9: num = "09";
        break;
    }
  }


  const getTime = () => {

    // Variables
    const date = new Date();
    let hour = date.getHours();
    let minute = date.getMinutes();
    let second = date.getSeconds();
    const day = date.getDay();
    const dayMonth = date.getDate();
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Switches
    setNum(hour);
    setNum(minute);
    setNum(second);
    setNum(dayMonth);



    setTime(`${hour} : ${minute} : ${second}`);
    setDay(`${day}, ${dayMonth}`);
  };



  return (
    <>
      <h1>{time}</h1>
      <h2>{day}</h2>
      {/* sky bg => based on time */}
      {/* Weather bg => based on weather app */}
      {/* Time */}
      {/* Date */}
      {/* Forecast */}
      {/* Dark Sky link */}
      {/* My name */}
    </>
  );
}

export default App;
