import React, { useState, useEffect } from 'react';
import axios from "axios";
import Background from "./components/Background.js";
import Time from "./components/Time.js";
import Moon from "./components/Moon.js";
import Forecast from "./components/Forecast.js";
import Today from "./components/Today.js";
import Hourly from "./components/Hourly.js";
import Daily from "./components/Daily.js";
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("");
  const [hour, setHour] = useState("");
  const [day, setDay] = useState("");
  const [daily, setDaily] = useState("");
  const [hourly, setHourly] = useState("");
  const [weather, setWeather] = useState("");
  const [zone, setZone] = useState("")
  const [temp, setTemp] = useState("")

  // Run scripts when App mounts
  useEffect(() => {
    getLocation();

    setInterval(() => {
      getTime();
    }, 1000);
  }, []);

  // Fahrenheit to Celsius
  const fToC = (temp) => {
    const celsius = Math.floor((temp - 32) / 1.8);
    return celsius;
  }

  // Render hourly forecast list
  const renderHourly = () => {
    if (hourly.data !== undefined) {
      let rendered = hourly.data.map((hour, i) => {
        return <Hourly hour={hour} fToC={fToC} key={i} />
      })
      return rendered;
    }
  }

  // Render daily forecast list
  const renderDaily = () => {
    if (daily.data !== undefined) {
      let rendered = daily.data.map((day, i) => {
        return <Daily day={day} fToC={fToC} key={i} i={i} />
      })
      return rendered;
    }
  }

  // Render Moon
  const renderMoon = () => {
    if (daily.data !== undefined) {
      let moon = <Moon moonPhase={daily.data[0].moonPhase} />
      return moon;
    }

  }

  const renderBackground = () => {
    let link;

    switch (true) {
      // Clear morning
      case (hour >= 5 && hour <= 9 && weather.icon === "clear-day"): link = "clear-morning.jpg";
        break;

      // Partly cloudy morning
      case (hour >= 5 && hour <= 9 && weather.icon === "partly-cloudy-day"): link = "clear-morning.jpg";
        break;

      // Clear day
      case (hour >= 9 && hour <= 18 && weather.icon === "clear-day"): link = "clear-day.jpg";
        break;

      // Partly cloudy day
      case (hour >= 9 && hour <= 18 && weather.icon === "clear-day"): link = "clear-day.jpg";
        break;

      // Rainy day
      case (hour >= 9 && hour <= 18 && weather.icon === "rain"): link = "rain-day.jpg";
        break;

      // Cloudy Day
      case (hour >= 9 && hour <= 18 && weather.icon === "cloudy"): link = "cloudy-day.jpg";
        break;

      // Rainy night
      case (hour >= 19 && hour <= 4 && weather.icon === "rain"): link = "rain-night.jpg";
        break;

      // Cloudy night
      case (hour >= 19 && hour <= 4 && weather.icon === "cloudy"): link = "cloudy-night.jpg";
        break;

      // Snowy day

      case (hour >= 5 && hour <= 18 && weather.icon === "snow"): link = "snow.jpg";
        break;

      // Default day
      case (hour >= 5 && hour <= 18): link = "clear-day.jpg";
        break;

      // Default night
      case (hour >= 19): link = "clear-night.jpg";
        break;
    }

    console.log(parseInt(hour))
    console.log(weather.icon);
    console.log(link)
  }

  // Fetch weather from API / Set state
  const getWeather = async (latitude, longitude) => {
    const forecast = await axios.get(`https://api.darksky.net/forecast/90f6dc370d1bbd73bc4e6216147431ff/${latitude},${longitude}`);

    setWeather(forecast.data.currently);
    setDaily(forecast.data.daily);
    setHourly(forecast.data.hourly);
    setZone(forecast.data.timezone);
    setTemp(fToC(forecast.data.currently.temperature));

  }

  // Changes single digit number into double. ie: 1 => 01
  const setNum = (num) => {
    let number = num;
    switch (num) {
      case 0: number = "00";
        break;
      case 1: number = "01";
        break;
      case 2: number = "02";
        break;
      case 3: number = "03";
        break;
      case 4: number = "04";
        break;
      case 5: number = "05";
        break;
      case 6: number = "06";
        break;
      case 7: number = "07";
        break;
      case 8: number = "08";
        break;
      case 9: number = "09";
        break;
    }

    return number;
  }


  // Get Current Time
  const getTime = () => {

    // Variables
    const date = new Date();
    let hour = setNum(date.getHours());
    let minute = setNum(date.getMinutes());
    let second = setNum(date.getSeconds());
    const day = date.getDay();
    let dayMonth = setNum(date.getDate());
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Set State
    setTime(`${hour} : ${minute} : ${second}`);
    setDay(`${days[day]}, ${dayMonth}`);
    setHour(hour);
  };



  // Get Location
  const getLocation = () => {
    let msg;
    const success = (position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;
      msg = "Updating your weather forecast..."
      // Passes location values to Weather API
      getWeather(latitude, longitude);
      console.log(msg)
    }

    const error = () => {
      "Helpppp"
    }

    if (!navigator.geolocation) {
      msg = 'Geolocation is not supported by your browser';
    } else {
      msg = "Locating…";
      navigator.geolocation.getCurrentPosition(success, error, { enableHighAccuracy: true, maximumAge: 10000 });
    }

    console.log(msg)
  }



  return (
    <>
      <Background />
      {renderBackground()}
      <Time time={time} />
      {renderMoon()}
      <Forecast weather={weather} zone={zone} temp={temp} />
      <Today day={day} daily={daily} fToC={fToC} />
      <div className="hourly-container">
        {renderHourly()}
      </div>
      <div className="daily-container">
        {renderDaily()}
      </div>
      <footer>
        <div>
          <a href="https://darksky.net/dev" rel="noopener noreferrer" target="_blank">Powered By Dark Sky API</a>
        </div>
        <div><a href="https://www.gautdel.com" rel="noopener noreferrer" target="_blank">GautDel</a></div>
      </footer>

    </>
  );
}

export default App;
