import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching...");
  const [day, setDay] = useState(" Fetching...");


  // run scripts with component mounts
  useEffect(() => {
    setInterval(() => {
      getTime();
    }, 1000);
  });


  const getTime = () => {

    // Variables
    const date = new Date();
    let hour = date.getHours();
    let minute = date.getMinutes();
    let second = date.getSeconds();
    const day = date.getDay();
    const dayMonth = date.getDate();
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Switches
    switch (hour) {
      case 0: hour = "00";
        break;
      case 1: hour = "01";
        break;
      case 2: hour = "02";
        break;
      case 3: hour = "03";
        break;
      case 4: hour = "04";
        break;
      case 5: hour = "05";
        break;
      case 6: hour = "06";
        break;
      case 7: hour = "07";
        break;
      case 8: hour = "08";
        break;
      case 9: hour = "09";
        break;
    }

    switch (minute) {
      case 1: minute = "01";
        break;
      case 2: minute = "02";
        break;
      case 3: minute = "03";
        break;
      case 4: minute = "04";
        break;
      case 5: minute = "05";
        break;
      case 6: minute = "06";
        break;
      case 7: minute = "07";
        break;
      case 8: minute = "08";
        break;
      case 9: minute = "09";
        break;
    }
    switch (second) {
      case 1: second = "01";
        break;
      case 2: second = "02";
        break;
      case 3: second = "03";
        break;
      case 4: second = "04";
        break;
      case 5: second = "05";
        break;
      case 6: second = "06";
        break;
      case 7: second = "07";
        break;
      case 8: second = "08";
        break;
      case 9: second = "09";
        break;
    }


    setTime(`${hour} : ${minute} : ${second}`);
    setDay(days[day], dayMonth);
  };



  return (
    <>
      <h1>{time}</h1>
      <h2>{day}</h2>
      {/* sky bg => based on time */}
      {/* Weather bg => based on weather app */}
      {/* Time */}
      {/* Date */}
      {/* Forecast */}
      {/* Dark Sky link */}
      {/* My name */}
    </>
  );
}

export default App;
