import React, { useState, useEffect } from 'react'


const Hourly = ({ hour, fToC }) => {
  const [time, setTime] = useState(new Date(hour.time * 1000));
  const [compHour, setCompHour] = useState(time.getHours());


  useEffect(() => {
    getIcon(hour.icon);
  })

  const getIcon = (icon) => {
    let img = "default-cloud.png";
    switch (icon) {
      case "clear-day": img = "clear-day.png";
        break;
      case "clear-night": img = "clear-night.png";
        break;
      case "rain": img = "rain.png";
        break;
      case "snow": img = "snow.png";
        break;
      case "sleet": img = "sleet.png";
        break;
      case "wind": img = "wind.png";
        break;
      case "fog": img = "fog.png";
        break;
      case "cloudy": img = "cloudy.png";
        break;
      case "partly-cloudy-day": img = "partly-cloudy-day.png";
        break;
      case "partly-cloudy-night": img = "partly-cloudy-night.png";
        break;
    }

    return img;
  }

  return (
    <div className="hour-card">
      <h6 className="hour">{compHour}</h6>
      <img src={window.location.origin + '/imgs/cloud-test.png'} />
      <h6 className="temp">{fToC(hour.temperature)}Â°</h6>
    </div>
  )
}

export default Hourly
