import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

  // State
  const [time, setTime] = useState();


  // run scripts with component mounts
  useEffect(() => {
    setInterval(() => {
      getTime();
    }, 1000);
  });

  // 
  const getTime = () => {
    let date = new Date();
    let hour = date.getHours();
    let minute = date.getMinutes();
    let second = date.getSeconds();
    setTime(`${hour}:${minute}:${second}`);
  };



  return (
    <>
      <h1>{time}</h1>
      {/* sky bg => based on time */}
      {/* Weather bg => based on weather app */}
      {/* Time */}
      {/* Date */}
      {/* Forecast */}
      {/* Dark Sky link */}
      {/* My name */}
    </>
  );
}

export default App;
