import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching time...");


  // run scripts with component mounts
  useEffect(() => {
    setInterval(() => {
      getTime();
    }, 1000);
  });


  const getTime = () => {
    // Variables
    let date = new Date();
    let hour = date.getHours();
    let minute = date.getMinutes();
    let second = date.getSeconds();
    let day = date.getDay();

