import React from 'react'

const Loader = ({ msg }) => {
  return (
    <div className="loader">
      <img className="sun" src={window.location.origin + "/imgs/icons/clear-day.png"} />
      <img className="moon" src={window.location.origin + "/imgs/icons/clear-night.png"} />

      <p>{msg}</p>
    </div>
  )
}

export default Loader
