import React, { useState, useEffect } from 'react';
import axios from "axios";
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching...");
  const [day, setDay] = useState(" Fetching...");
  const [weather, setWeather] = useState("Fetching Weather...");
  const [lat, setLat] = useState(null);
  const [long, setLong] = useState(null);


  // run scripts with component mounts
  useEffect(() => {
    if (lat && long !== null) getWeather();
    getLocation();
    setInterval(() => {
      getTime();
    }, 1000);
  }, []);

  const getWeather = async () => {
    const forecast = await axios.get(`https://api.darksky.net/forecast/90f6dc370d1bbd73bc4e6216147431ff/${lat},${long}`);

    setWeather(forecast.data.currently);
    console.log(forecast)
  }

  // Changes single digit number into double. ie: 1 => 01
  const setNum = (num) => {
    let number = num;
    switch (num) {
      case 0: number = "00";
        break;
      case 1: number = "01";
        break;
      case 2: number = "02";
        break;
      case 3: number = "03";
        break;
      case 4: number = "04";
        break;
      case 5: number = "05";
        break;
      case 6: number = "06";
        break;
      case 7: number = "07";
        break;
      case 8: number = "08";
        break;
      case 9: number = "09";
        break;
    }

    return number;
  }


  // Get Current Time
  const getTime = () => {
    // Variables
    const date = new Date();
    let hour = setNum(date.getHours());
    let minute = setNum(date.getMinutes());
    let second = setNum(date.getSeconds());
    const day = date.getDay();
    let dayMonth = setNum(date.getDate());
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Set State
    setTime(`${hour} : ${minute} : ${second}`);
    setDay(`${days[day]}, ${dayMonth}`);
  };



  // Get Location
  const getLocation = () => {
    let msg;
    const success = (position) => {
      const latitude = position.coords.latitude;
      const longitude = position.coords.longitude;

      setLong(longitude);
      setLat(latitude);
    }

    const error = () => {
      "Helpppp"
    }

    if (!navigator.geolocation) {
      msg = 'Geolocation is not supported by your browser';
    } else {
      msg = 'Locating…';
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }



  return (
    <>
      <h1>{time}</h1>
      <h2>{day}</h2>
      <h2>{lat}</h2>
      <h2>{long}</h2>
      <h2>{weather.summary}</h2>

      {/* Weather bg => based on weather app */}
      {/* Time */}
      {/* Date */}
      {/* Forecast */}
      {/* Dark Sky link */}
      {/* My name */}
    </>
  );
}

export default App;
