import React, { useState, useEffect } from 'react';
import axios from "axios";
import './App.css';

function App() {

  // State
  const [time, setTime] = useState("Fetching...");
  const [day, setDay] = useState(" Fetching...");
  // const weather = [weather, setWeather] = useState("Fetching Weather...");


  // run scripts with component mounts
  useEffect(() => {
    getWeather();
    setInterval(() => {
      getTime();
    }, 1000);
  }, []);

  const getWeather = async () => {
    const weather = await axios.get('https://api.darksky.net/forecast/90f6dc370d1bbd73bc4e6216147431ff/37.8267,-122.4233');
    console.log(weather);
  }

  // Changes single digit number into double. ie: 1 => 01
  const setNum = (num) => {
    let number = num;
    switch (num) {
      case 0: number = "00";
        break;
      case 1: number = "01";
        break;
      case 2: number = "02";
        break;
      case 3: number = "03";
        break;
      case 4: number = "04";
        break;
      case 5: number = "05";
        break;
      case 6: number = "06";
        break;
      case 7: number = "07";
        break;
      case 8: number = "08";
        break;
      case 9: number = "09";
        break;
    }

    return number;
  }


  const getTime = () => {

    // Variables
    const date = new Date();
    let hour = setNum(date.getHours());
    let minute = setNum(date.getMinutes());
    let second = setNum(date.getSeconds());
    const day = date.getDay();
    let dayMonth = setNum(date.getDate());
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    // Set State
    setTime(`${hour} : ${minute} : ${second}`);
    setDay(`${days[day]}, ${dayMonth}`);
  };



  return (
    <>
      <h1>{time}</h1>
      <h2>{day}</h2>
      {/* sky bg => based on time */}
      {/* Weather bg => based on weather app */}
      {/* Time */}
      {/* Date */}
      {/* Forecast */}
      {/* Dark Sky link */}
      {/* My name */}
    </>
  );
}

export default App;
